    s: "",
    results: [],
    selected: {}
  })

  //omdb api key
  const apiurl = "http://www.omdbapi.com/?i=tt3896198&apikey=6c68ec5d";

  const handlInput = (e) => {
    let s = e.target.value;

    setState(previousState => {
      return { ...previousState, s: s }
    });

    console.log(state.s);
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>Movie Database</h1>
        <main>
          <Search />

        </main>
      </header>
    </div>
  );
}

export default App;
